import styles from '../components/Index.module.css' 
import Head from 'next/head'


export default function Index(){

    return (
        <>
            <Head>
                <meta charSet="UTF-8"/>
                <meta name="viewport" content="width=device-width, initial-scale=1.0" />
                <title>Ferrari</title>
            </Head>
            <header id="header">
                <div id="overlay" data-close="menu"></div>
                <a href="/"><img src="images/logo.svg" id="logo" alt="Logo" /></a>
                <button type="button" id="btn-open">
                    <svg id="menu-black-18dp" xmlns="http://www.w3.org/2000/svg" width="32" height="32" viewBox="0 0 32 32">
                   
                        <path id="Caminho_215" data-name="Caminho 215" d="M0,0H32V32H0Z" fill="none"/>
                        <path id="Caminho_216" data-name="Caminho 216" d="M3,18H29V16H3Zm0-5H29V11H3ZM3,6V8H29V6Z" transform="translate(0 4)"/>
                    </svg>
                </button>
                <nav className={styles.menu}>
                    <div>
                        <button type="button" id="btn-close" data-close="menu">
                            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24">
                                
                                <path id="caminho" d="M19,6.41,17.59,5,12,10.59,6.41,5,5,6.41,10.59,12,5,17.59,6.41,19,12,13.41,17.59,19,19,17.59,13.41,12Z"/>
                                <path d="M0,0H24V24H0Z" fill="none"/>
                            </svg>
                        </button>
                        <ul>
                            <li><a href="/">Home</a></li>
                            <li><a href="/our-history.html">Nossa História</a></li>
                            <li><a href="/gallery.html">Galeria</a></li>
                            <li><a href="/contact.html">Contato</a></li>
                        </ul>
                    </div>
                    <a href="register.html" className={styles.btnRegister}>
                        Registre-se / Login
                    </a>
                </nav>
            </header>
            <main id="home">        
                <hr className={styles.italy} />
                <header>
                    
                    <h1>SF90 Stradale</h1>
                    <a href="/galeria.html">Detalhes</a>

                </header>
                <section className={styles.gallery}>
                    <figure>
                        <img src="/images/photos/espostivas-amarela-375x250.jpg" alt="Ferrari Amarela" />	
                        <figcaption>
                            <h2>Esportivas</h2>
                            <p>Ferrari F1000</p>
                        </figcaption>
                    </figure>

                    <figure>
                        <img src="/images/photos/esportivas-interior-375x250.jpg" alt="Ferrari Interior" />	
                        <figcaption>
                            <h2>Esportivas</h2>
                            <p>Ferrari F1000</p>
                        </figcaption>
                    </figure>

                    <figure>
                        <img src="/images/photos/espostivas-azul-375x250.jpg" alt="Ferrari Azul" />	
                        <figcaption>
                            <h2>Esportivas</h2>
                            <p>Ferrari F1000</p>
                        </figcaption>
                    </figure>

                </section>
                <section className={styles.inspiration}>

                    <div></div>

                    <div className={styles.text}>
                        <h2>Inspirando<span>Gerações</span></h2>
                        <hr />
                        <p>Assertively iterate enterprise-wide portals through synergistic products. Efficiently build adaptive schema…</p>
                        <p className="complete">Lorem ipsum dolor sit, amet consectetur adipisicing elit. Ab itaque nesciunt sapiente optio nobis atque veritatis rem aut ea expedita, reprehenderit fugit reiciendis, minus quod obcaecati inventore at placeat provident!</p>
                    </div>

                    <div className="car animated">
                        <div className="wheel"></div>
                    </div>

                </section>

                <section className="know">

                    <div className="know-1">
                        <h1>Conheça a Máquina</h1>
                        <p>Assertively iterate enterprise-wide portals through synergistic products. Efficiently build adaptive schemas after transparent quality vectors. Phosfluorescently optimize competitive resources after extensive convergence. Rapidiously optimize high-quality meta-services via distributed architectures. Credibly deliver 24/365.</p>
                        <a href="/gallery.html">Detalhes</a>
                    </div>

                    <img src="/images/top-view.jpg" alt="Ferrari Top View" />

                    <div className="know-2">
                        
                        <dl>
                            <dt>Motorização</dt>
                            <dd>Assertively iterate enterprise-wide portals through synergistic products.</dd>

                            <dt>Desempenho</dt>
                            <dd>Assertively iterate enterprise-wide portals through synergistic products.</dd>

                            <dt>Conforto</dt>
                            <dd>Assertively iterate enterprise-wide portals through synergistic products.</dd>
                        </dl>

                    </div>

                </section>
            </main>
            <footer id="footer">
                <button type="button">
                    <img src="images/icon-arrow-up.svg" alt="up" />
                </button>
                <div className="columns">
                    <div>
                        <ul className="links">
                            <li className="title">Links</li>
                            <li><a href="/">Home</a></li>
                            <li><a href="/our-history.html">Nossa História</a></li>
                            <li><a href="/gallery.html">Galeria</a></li>
                            <li><a href="/contact.html">Contato</a></li>
                        </ul>
                        <ul>
                            <li className="title">Contato</li>
                            <li>E-mail: support@ferrari.it</li>
                            <li className="social">
                                <a href="#"><img src="images/facebook.svg" alt="Facebook"/></a>
                                <a href="#"><img src="images/twitter.svg" alt="Twitter"/></a>
                                <a href="#"><img src="images/youtube.svg" alt="YouTube"/></a>
                                <a href="#"><img src="images/instagram.svg" alt="Instagram"/></a>
                            </li>
                        </ul>
                    </div>
                    <a href="/"><img src="images/ferrari-footer.svg" alt="Logo" className="logo" /></a>
                </div>
                <p>2020 © Hcode. All rights reserved.</p>
            </footer>
            <style jsx>{`
    * {
        box-sizing: border-box;
        scroll-behavior: smooth;
    }
    
    body, div, article, aside, footer, header, main, nav, section, ul, ol, li, button, figure, picture {
        display: flex;
        box-sizing: border-box;
        scroll-behavior: smooth;

    }

    body {
        margin: 0;
        flex-direction: column;
        font-family: 'Open Sans';
        font-style: normal;
        font-weight: 400;
        background-color: var(--bg-color);
    }

    hr.italy {
        background-image: url('/images/bg-italy.svg');
        background-repeat: repeat-x;
        width: 100%;
        height: 18px;
        margin: 0;
        border: none;
    }

    main {
        min-height: calc(100vh - 468px);
    }

    .page {
        flex-direction: column;
    }
    .page .page-header {
        width: 100%;
        flex-direction: column;
    }
    .page header {
        height: 210px;
        width: 100%;
        background-size: cover;
        filter: brightness(0.5);
        flex-direction: column;
        justify-content: flex-end;
        color: var(--white);
        padding: 1em;
    }

    .page header ul,
    .page header li, 
    .page header h1 {
        margin: 0;
        padding: 0;
    }

    form {
        flex-direction: column;
        padding: 1em;
        width: 100%;
    }

    form .fields {
        flex-direction: column;
    }

    form .field {
        position: relative;
        flex-direction: column;
        width: 100%;
        margin-bottom: 1em;
    }

    form .field label {
        color: var(--grayC);
        font-size: 14px;
        position: absolute;
        z-index: 2;
        left: 10px;
        top: 10px;
        transition: color .3s ease-in-out;
    }

    form .field input,
    form .field textarea {
        line-height: 1.5em;
        padding: 10px;
        padding-top: 30px;
        font-size: 14px;
        color: var(--gray5);
        outline: none;
        font-family: 'Open Sans';
        border: var(--grayC) 2px solid;
        border-radius: 5px;
        position: relative;
        z-index: 1;
        top: 0;
        transition: border-color .3s ease-in-out;
    }

    form .field input:focus,
    form .field textarea:focus {
        border-color: var(--green);
    }

    form .field input:focus + label,
    form .field textarea:focus + label {
        color: var(--green);
    }

    form .actions {
        flex-direction: column;
        align-content: center;
    }
    form .actions a {
        display: block;
        text-align: center;
        padding: 1em;
        color: var(--gray6);
    }

    form button {
        width: 100%;
        background-color: var(--green);
        color: var(--white);
        border: none;
        padding: .5em 1em;
        justify-content: center;
        align-items: center;
        font-size: 24px;
        font-weight: bold;
        border-radius: 5px;
        outline: none;
        text-transform: uppercase;
    }

    form button:hover {
        background-color: var(--green-hover);
    }

    form button:active {
        background-color: var(--green-active);
    }

    a {
        color: var(--red);
    }
    a:hover {
        color: var(--black);
    }

    .show-desktop {
        display: none;
    }

    @media (min-width: 576px) {
        .page header {
            height: 20vw;
            min-height: 300px;
        }
    }

    @media (min-width: 992px) {
        .page .page-header {
            width: 100%;
            flex-direction: column-reverse;
            height: 18px;
        }
        .page header  {
            filter: none;
            align-items: flex-end;
        }
        .page header li {
            font-size: 30px;
        }
        .page header h1 {
            font-size: 100px;
            text-shadow: 0 3px 6px var(--black15);
        }
        form .fields {
            flex-direction: row;
        }
        form .fields .field {
            margin-right: 1em;
        }
        form .fields .field:last-child {
            margin-right: 0;
        }
        form .actions {
            flex-direction: row;
            justify-content: space-between;
        }
        form .actions a {
            text-align: right;
        }
        form .field textarea {
            min-height: 250px;
        }
        form button {
            max-width: 340px;
        }
    }

    @media (min-width: 1200px) {
        main {
            min-height: calc(100vh - 378px);
        }
        .show-desktop {
            display: flex;
        }
        .hide-desktop {
            display: none;
        }
    }

    @media (prefers-color-scheme: dark) {
        .page header {
            background-color: var(--white50);
            color: var(--black);
        }
        form .field label {
            color: var(--gray6);
        }
        form .field input,
        form .field textarea {
            background-color: var(--bg-color);
            border: var(--gray6) 2px solid;
            color: var(--gray3);
        }
        form button {
            color: var(--black);
        }
        .page header h1 {
            text-shadow: 0 3px 6px var(--dark);
        }
    }


    :root {
        --bg-color: #FFF;
        --black: #000;
        --white: #FFF;
        --red: #E32119;
        --yellow: #FFF100;
        --green: #00994D;
        --green-hover: #008744;
        --green-active: #00D169;
        --dark: #343433;
        --light: #F8F8F8;
        --header: #E5E3DF75;
        --white50: #FFFFFF50;
        --black75: rgba(0, 0, 0, .75);
        --black50: rgba(0, 0, 0, .5);
        --black15: rgba(0, 0, 0, .15);
        --gray3: #333;
        --gray5: #555;
        --gray6: #666;
        --grayC: #CCC;
    }

    @media (prefers-color-scheme: dark) {
        :root {
            --bg-color: #35363A;
            --black: #FFF;
            --white: #000;
            --dark: #202124;
            --light: #35363A;
            --header: #202124;
            --white50: #00000050;
            --black75: rgba(255, 255, 255, .75);
            --black50: rgba(255, 255, 255, .5);
            --black15: rgba(255, 255, 255, .15);
            --gray3: #CCC;
            --gray5: #999;
            --gray6: #777;
            --grayC: #333;
        }
    }

    `}</style>
        </>
    )


}
